mapper.update:

update demo_user
set del_flag = 1, update_time = now()
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from demo_user
where del_flag = 0

  and id = #{id,jdbcType=VARCHAR}


  and username like CONCAT('%', #{username,jdbcType=VARCHAR}, '%')


  and realname like CONCAT('%', #{realname,jdbcType=VARCHAR}, '%')


  and age = #{age,jdbcType=INTEGER}


  and birthday = #{birthday,jdbcType=DATE}


  and birthday >= #{birthdayBegin,jdbcType=BIGINT}


  and birthday <= #{birthdayEnd,jdbcType=BIGINT}


  and (
  username like CONCAT('%', #{searchContent,jdbcType=VARCHAR}, '%')
  or realname like CONCAT('%', #{searchContent,jdbcType=VARCHAR}, '%')
  )

order by create_time desc
